-- ----------------------------
-- Table structure for t_scripture_barrage_progress
-- ----------------------------
DROP TABLE IF EXISTS `t_scripture_barrage_progress`;
CREATE TABLE `t_scripture_barrage_progress`  (
  `id` bigint(20) NOT NULL COMMENT '主键，雪花ID',
  `user_id` bigint(20) NOT NULL COMMENT '用户ID',
  `scripture_id` bigint(20) NOT NULL COMMENT '典籍ID',
  `last_offset` int(11) NOT NULL DEFAULT 0 COMMENT '全局  \r\n  偏移',
  `last_section_id` bigint(20) NULL DEFAULT NULL COMMENT '当前卷/章ID',
  `section_offset` int(11) NOT NULL DEFAULT 0 COMMENT '卷内  \r\n  偏移',
  `last_fetch_limit` int(11) NULL DEFAULT NULL COMMENT '最近一次获取    \r\n  的limit',
  `last_fetch_time` datetime(0) NULL DEFAULT NULL COMMENT '最近一次获取    \r\n  时间',
  `is_daily_reset` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否每\r\n  日重置进度 0/1',
  `create_by` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '创建人',
  `create_time` datetime(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '创建时间',
  `update_by` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '更新人',
  `update_time` datetime(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间',
  `is_deleted` tinyint(1) NOT NULL DEFAULT 0 COMMENT '逻辑删\r\n  除 0/1',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_user_scripture`(`user_id`, `scripture_id`, `is_deleted`) USING BTREE,
  INDEX `idx_scripture`(`scripture_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '弹幕阅读进度' ROW_FORMAT = Dynamic;

