-- ----------------------------
-- Table structure for t_scripture_section
-- ----------------------------
DROP TABLE IF EXISTS `t_scripture_section`;
CREATE TABLE `t_scripture_section`  (
  `id` bigint(20) NOT NULL COMMENT '主键ID，雪花算法生成',
  `scripture_id` bigint(20) NOT NULL COMMENT '典籍ID',
  `section_no` int(11) NOT NULL COMMENT '分段序号（从1开始，按卷/章顺序）',
  `title` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '分段标题（如：第X卷）',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '分段正文（可存Markdown）',
  `audio_url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '分段音频URL',
  `word_count` int(11) NULL DEFAULT 0 COMMENT '该分段字数',
  `duration_seconds` int(11) NULL DEFAULT 0 COMMENT '朗读/播放时长（秒，可选）',
  `is_free` tinyint(4) NULL DEFAULT 0 COMMENT '是否可试读：0-否，1-是',
  `sort_order` int(11) NULL DEFAULT 0 COMMENT '排序序号，通常与 section_no 一致',
  `create_time` datetime(0) NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '创建时间',
  `create_by` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `update_by` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `update_time` datetime(0) NULL DEFAULT CURRENT_TIMESTAMP(0) ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间',
  `is_deleted` tinyint(1) NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`) USING BTREE,
