-- Table structure for t_user_scripture_purchase_backup
-- ----------------------------
DROP TABLE IF EXISTS `t_user_scripture_purchase_backup`;
CREATE TABLE `t_user_scripture_purchase_backup`  (
  `id` bigint(20) NOT NULL COMMENT '主键ID，雪花算法生成',
