  `last_reading_position` int(11) NULL DEFAULT 0 COMMENT '最后阅读位置（字符位置）',
  `create_by` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT 'system' COMMENT '创建人',
