mapper.select:
SELECT
,
u.nickname, u.avatar_url
FROM t_ranking r
LEFT JOIN t_user u ON r.user_id = u.id
WHERE r.rank_type = #{rankType}
AND r.snapshot_date = #{snapshotDate}
AND r.is_deleted = 0
ORDER BY r.ranking_position ASC

    LIMIT #{limit}

SELECT
,
u.nickname, u.avatar_url
FROM t_ranking r
LEFT JOIN t_user u ON r.user_id = u.id
WHERE r.user_id = #{userId}
AND r.rank_type = #{rankType}
AND r.snapshot_date = #{snapshotDate}
AND r.is_deleted = 0
LIMIT 1
